-- Table structure for codelist
-- ----------------------------
DROP TABLE IF EXISTS `codelist`;
CREATE TABLE `codelist` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL,
  `content` text COMMENT '软件说明',
  `introduce` varchar(255) DEFAULT NULL COMMENT '软件介绍',
  `big` varchar(255) DEFAULT NULL COMMENT '软件大小',
  `sendTime` int(11) DEFAULT NULL COMMENT '发表时间',
  `link` varchar(255) DEFAULT NULL,
  `keywords` varchar(255) DEFAULT NULL COMMENT '关键字',
  `user_id` int(11) DEFAULT NULL COMMENT '谁发表的id',
  `sight` int(11) DEFAULT NULL COMMENT '浏览量',
  `look` varchar(255) DEFAULT NULL COMMENT '演示',
  `sort` int(11) DEFAULT NULL COMMENT '分类id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

