-- ----------------------------
-- Records of procourse
-- ----------------------------
INSERT INTO `procourse` VALUES ('1', '4', '刘玉', '布尔教育名师。以网名燕十八在培训领域授业解惑，国内知名PHP讲师，PHP传世经典四部流传甚广。', null, '0', '1475737438', '0', '1', '网页开发基础', '57f5f75eaf13f.jpg', '一说学网页,学编程,大家都下意识觉得要\"吃苦\",\"熬夜\",哪有那么复杂,一定是你看错了教程. 挑一个阳光的午后,关掉手机,用6个小时,看看十八哥如何把一个公司的保安教会HTML,Div,CSS的. 课程共8小时,57小集,轻松一点,你将马上学会.', '学会应用HTML知识', '57课时,每课5-10分钟,8小时学会Div+CSS', '0');
INSERT INTO `procourse` VALUES ('2', '9', '罗宇', '拥有长达八年的PHP教学经验', null, '0', '1475738767', '0', '1', 'PHP基础课程', '57f5fc8f89b9d.jpg', '将会带领大家从零基础入门学会php。', '没有基础的人学会PHP。', '按课程进行教学。', '0');

