-- ----------------------------
-- Records of programme
-- ----------------------------
INSERT INTO `programme` VALUES ('2', '57f5d31a15804.jpg', '57f5d31a5a1bd.jpg', 'ios', 'iOS是由苹果公司开发的移动操作系统。原名叫iPhone OS，因为iPad，iPhone，iPod touch都使用iPhone OS，所以2010WWDC大会上宣布改名为iOS。', '1475728154', '3', '1');
INSERT INTO `programme` VALUES ('4', '57f5ddaf510fc.jpg', '57f5ddaf5209d.jpg', 'HTML5', '万维网的核心语言、标准通用标记语言下的一个应用超文本标记语言（HTML）的第五次重大修改.', '1475730863', '8', '1');
INSERT INTO `programme` VALUES ('5', '57f5dde54695b.jpg', '57f5dde547513.jpg', 'Linux', 'Linux是一套免费使用和自由传播的类Unix操作系统，是一个基于POSIX和UNIX的多用户、多任务、支持多线程和多CPU的操作系统。它能运行主要的UNIX工具软件、应用程序和网络协议。它支持32位和64位硬件。', '1475730917', '10', '1');
INSERT INTO `programme` VALUES ('6', '57f5de1fe80c6.jpg', '57f5de1fe9066.jpg', '.NET', 'NET是 Microsoft XML Web services 平台。是微软开通服务于动态网站的搭建而产生的一种语言。', '1475730975', '12', '1');
INSERT INTO `programme` VALUES ('7', '57f5de5718528.jpg', '57f5de57194c8.jpg', 'C++', 'C++是在C语言的基础上开发的一种面向对象编程语言，属于编译型语言，应用广泛；C++支持多种编程范式 －－面向对象编程、泛型编程和过程化编程。', '1475731031', '14', '1');
INSERT INTO `programme` VALUES ('8', '57f5de93036e3.jpg', '57f5de9305623.jpg', 'C语言', 'C语言是一门通用计算机编程语言，应用广泛。C语言的设计目标是提供一种能以简易的方式编译、处理低级存储器、产生少量的机器码以及不需要任何运行环境支持便能运行的编程语言。', '1475731090', '13', '1');
INSERT INTO `programme` VALUES ('9', '57f5e76054b36.jpg', '57f5e76055ebf.jpg', 'PHP', 'PHP是一种通用开源脚本语言。语法吸收了C语言、Java和Perl的特点，利于学习，使用广泛，主要适用于Web开发领域。PHP 独特的语法混合了C、Java、Perl以及PHP自创的语法。', '1475733344', '5', '1');

