INSERT INTO `question` VALUES ('1', 'java开发', '<p>我的问题描述都写在这里了，所以你要多了解一下JAVA的知识。</p>', '1', '5', '1473649101', '1474538836', '0', '0', null);
INSERT INTO `question` VALUES ('2', '安卓开发和JAVA开发是同样的吗？', '<p>我挺想知道安卓开发是不是和自己的开发是一样的呢？希望可以得到大家的帮助。谢谢了。</p>', '2', '5', '1473649101', '1474538907', '0', '0', null);
INSERT INTO `question` VALUES ('3', 'PHP难学吗？', '<p>为什么那么多人学习PHP，学了PHP出来工作难找吗？求解。</p>', '1', '0', '1473649101', '1474538990', '0', '0', null);
INSERT INTO `question` VALUES ('5', 'PHP问题求解', '<p>请问一下有没有办法获取用户的PHP的ip地址。</p>', '5', '5', '1473649101', '1474543018', '0', '0', null);
INSERT INTO `question` VALUES ('9', '我想知道node.js难学吗？', '<p>求推荐node.js的教学视频，谢谢了。</p>', '7', '1', '1474628607', '1474629204', '0', '10', null);
INSERT INTO `question` VALUES ('10', '如何学习jquery', '<p>挺想问下有没有很好的jquery学习方法？</p>', '6', '12', '1474629509', '1474629642', '0', '10', null);
INSERT INTO `question` VALUES ('11', '你们是如何弄thinkphp框架的？', '<p>有没有大神会thinkphp的？</p>', '5', '6', '1474629509', '1474629877', '0', '10', null);
INSERT INTO `question` VALUES ('12', 'php的面向对象好用吗？', '<p>求介绍。</p>', '5', '18', '1474630402', '1474632215', '0', '100', null);
INSERT INTO `question` VALUES ('13', '测试的软件', '<p>PHP的开发测试。</p>', '5', '12', '1474630402', '1474632237', '0', '10', null);
