INSERT INTO `soft` VALUES ('29', 'Eclipse是一个开放源代码的、基于 Java 的可扩展开发平台。就其本身而言，它只是一个框架和一组服务，用于通过插件组件构建开发环境，幸运的是，Eclipse 附带了一个标准的插件集，包括 Java 开发工具（Java Development Tools,JDT），虽然大多数用户很乐于将 Eclipse当Java IDE来使用，但 Eclipse 的目标不仅限于此，Eclipse还包括插件开发环境（Plug-in Development Environment,PDE），这个组件主要针对希望扩 Ecl', null, '183.2M', '2016-08-21 23:17:07', '编辑器', '中文', 'sun公司', null, '免费', null, '0', '0', 'http://dlsw.baidu.com/sw-search-sp/soft/fa/25046/eclipse-SDK-421.1394615537.zip', ' Eclipse SDK', '0', 'JAVA开发环境,JAVA编辑器', '1', '0', '1');
