DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user` varchar(25) DEFAULT NULL,
  `pass` varchar(50) DEFAULT NULL,
  `introduce` varchar(255) DEFAULT 'hello world.',
