-- Table structure for userintro
-- ----------------------------
DROP TABLE IF EXISTS `userintro`;
CREATE TABLE `userintro` (
  `usid` int(11) NOT NULL AUTO_INCREMENT COMMENT '用户id',
  `nicheng` varchar(16) DEFAULT NULL,
  `headimg` varchar(255) DEFAULT NULL,
  `about` varchar(120) DEFAULT NULL COMMENT '自我介绍',
  `user_id` int(11) DEFAULT NULL COMMENT '用户id',
  `isOne` int(11) DEFAULT '0',
  `bmoney` int(11) DEFAULT '100' COMMENT 'B币',
  `jifen` int(11) DEFAULT '0' COMMENT '积分',
  `lev` int(11) DEFAULT '1' COMMENT '等级id关联',
  `know` int(11) DEFAULT NULL COMMENT '擅长领域',
  `exp` int(11) DEFAULT '0' COMMENT '经验值',
