-- ----------------------------
-- View structure for about_user
-- ----------------------------
DROP VIEW IF EXISTS `about_user`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `about_user` AS select u.*,ul.* from userintro ul,userlist u where ul.user_id = u.idnumber ;

-- ----------------------------
-- View structure for article_list
-- ----------------------------
DROP VIEW IF EXISTS `article_list`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `article_list` AS select a.* ,s.state,s.paixu,c.category from article a ,category c,state s where a.cid = c.id and a.sid = s.id ;

-- ----------------------------
-- View structure for blog_article
-- ----------------------------
DROP VIEW IF EXISTS `blog_article`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `blog_article` AS select b.* ,i.arcimg,a.nicheng from blog_cate b,introduce i ,aboutme a where b.user_id = a.user_id and i.user_id = b.user_id ;

-- ----------------------------
-- View structure for blog_cate
-- ----------------------------
DROP VIEW IF EXISTS `blog_cate`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `blog_cate` AS SELECT
	b.*, c.category
FROM
	blog b,
	cate c
WHERE
	b.cate_id = c.caid ;

-- ----------------------------
-- View structure for blog_comment
-- ----------------------------
DROP VIEW IF EXISTS `blog_comment`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `blog_comment` AS SELECT
	b.lid, c.*, a.nicheng,
	i.arcimg
FROM
	blog b,
	COMMENT c,
	aboutme a,
	introduce i
WHERE
	b.lid = c.arc_id 
AND
	c.cwho = a.user_id
And 
	c.cwho = i.user_id ;

-- ----------------------------
-- View structure for blog_xinxi
-- ----------------------------
DROP VIEW IF EXISTS `blog_xinxi`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `blog_xinxi` AS select m.cid,m.nicheng,m.comment,m.cwho,m.arc_id,m.arcimg,m.pass,b.* from blog_comment m,blog b where b.lid = m.lid ;

-- ----------------------------
-- View structure for blog_zan
-- ----------------------------
DROP VIEW IF EXISTS `blog_zan`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `blog_zan` AS select b.*,p.zan,p.who from blog_article b,perfect p where p.lid = b.lid ;

-- ----------------------------
-- View structure for code_list
-- ----------------------------
DROP VIEW IF EXISTS `code_list`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `code_list` AS select c.*,cs.sort fl,u.nicheng,u.user,u.power from codelist c,codesort cs,user u where c.sort = cs.cid and c.user_id = u.id ;

-- ----------------------------
-- View structure for community_list
-- ----------------------------
DROP VIEW IF EXISTS `community_list`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `community_list` AS SELECT
	q.*, t.*, o.*, u.nicheng,u.headimg,u.about,u.isOne,u.bmoney,u.jifen,u.lev,u.know,u.exp
FROM
	community q,
	questiontype t,
	offer o,
	userintro u
WHERE
	q.oid = o.money
AND q.qtype = t.tid
AND q.user_id = u.user_id ;

-- ----------------------------
-- View structure for com_comlist
-- ----------------------------
DROP VIEW IF EXISTS `com_comlist`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `com_comlist` AS SELECT
	qc.*, q.qcontent,
	q.qtitle
FROM
	com_comment qc,
	community q
WHERE
	qc.ques_id = q.qid ;

-- ----------------------------
-- View structure for com_comment
-- ----------------------------
DROP VIEW IF EXISTS `com_comment`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `com_comment` AS SELECT
	q.qid,
	q. comment,
	q.ques_id,
	q.send_id,
	q.sendTime,
	u.*
FROM
	comcoment q,
	userintro u
WHERE
	q.send_id = u.user_id ;

-- ----------------------------
-- View structure for course_list
-- ----------------------------
DROP VIEW IF EXISTS `course_list`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `course_list` AS SELECT
	p.*, z.zname,
	c.course,
	c.isOver,
	a.zname kname
FROM
	procourselist p,
	procate z,
	procourse c,
	procategory a
WHERE
	p.zid = z.id
AND p.pid = c.id
AND p.zid = a.zid ;

-- ----------------------------
-- View structure for fans_fans
-- ----------------------------
DROP VIEW IF EXISTS `fans_fans`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `fans_fans` AS SELECT
	a.nicheng,
	a.live,
	a.introduce,
	a.sex,
	i.arcimg,
	f.*
FROM
	aboutme a,
	introduce i,
	fans f
WHERE
	f.user_id = a.user_id
AND i.user_id = f.user_id ;

-- ----------------------------
-- View structure for fans_user
-- ----------------------------
DROP VIEW IF EXISTS `fans_user`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `fans_user` AS SELECT
	a.nicheng,
	a.live,
	a.introduce,
	a.sex,
	i.arcimg,
	f.*
FROM
	aboutme a,
	introduce i,
	fans f
WHERE
	f.fans_id = a.user_id
AND i.user_id = f.fans_id ;

-- ----------------------------
-- View structure for message_intro
-- ----------------------------
DROP VIEW IF EXISTS `message_intro`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `message_intro` AS select m.* ,i.arcimg,a.nicheng from messageboard m,introduce i,aboutme a where m.person_id = i.user_id and m.person_id = a.user_id ;

-- ----------------------------
-- View structure for procategory_list
-- ----------------------------
DROP VIEW IF EXISTS `procategory_list`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `procategory_list` AS select p.*,c.course,a.zname kname from procategory p,procourse c,procate a where p.pid = c.id and p.zid = a.id ;

-- ----------------------------
-- View structure for procourse_list
-- ----------------------------
DROP VIEW IF EXISTS `procourse_list`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `procourse_list` AS select p.*,q.title,q.typename from procourse p,programme_list q where p.pid = q.id ;

-- ----------------------------
-- View structure for programme_list
-- ----------------------------
DROP VIEW IF EXISTS `programme_list`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `programme_list` AS select p.*,q.typename,u.nicheng,u.user,u.headimg from programme p,questiontype q,user u where p.sort = q.tid and u.id = p.user_id ;

-- ----------------------------
-- View structure for propit_list
-- ----------------------------
DROP VIEW IF EXISTS `propit_list`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `propit_list` AS select p.*,b.title,b.typename,u.headimg,u.nicheng,u.user from propit p,programme_list b,user u where p.bid = b.id and u.id = p.who ;

-- ----------------------------
-- View structure for pro_course
-- ----------------------------
DROP VIEW IF EXISTS `pro_course`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `pro_course` AS select p.*,a.sort from procourse p,programme a where p.pid = a.id ;

-- ----------------------------
-- View structure for question_list
-- ----------------------------
DROP VIEW IF EXISTS `question_list`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `question_list` AS SELECT
	q.*, t.*, o.*, u.nicheng,u.headimg,u.about,u.isOne,u.bmoney,u.jifen,u.lev,u.know,u.exp
FROM
	question q,
	questiontype t,
	offer o,
	userintro u
WHERE
	q.oid = o.money
AND q.qtype = t.tid
AND q.user_id = u.user_id ;

-- ----------------------------
-- View structure for ques_comlist
-- ----------------------------
DROP VIEW IF EXISTS `ques_comlist`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `ques_comlist` AS select qc.*,q.qcontent,q.qtitle from ques_comment qc,question q where qc.ques_id = q.qid ;

-- ----------------------------
-- View structure for ques_comment
-- ----------------------------
DROP VIEW IF EXISTS `ques_comment`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `ques_comment` AS select q.qid,q.comment,q.ques_id,q.send_id,q.sendTime,q.order,u.* from quescoment q,userintro u where q.send_id = u.user_id ;

-- ----------------------------
-- View structure for soft_list
-- ----------------------------
DROP VIEW IF EXISTS `soft_list`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `soft_list` AS select so.*,s.state,u.nicheng from user u,state s,soft so where so.user_id = u.id and so.sid = s.id ;

-- ----------------------------
-- View structure for user_about
-- ----------------------------
DROP VIEW IF EXISTS `user_about`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `user_about` AS select u.*,a.nicheng from userlist u,aboutme a where u.idnumber = a.user_id ;

-- ----------------------------
-- View structure for user_lev
-- ----------------------------
DROP VIEW IF EXISTS `user_lev`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `user_lev` AS select u.*,l.* from userintro u ,level l where u.lev = l.lid ;

-- ----------------------------
-- View structure for user_list
-- ----------------------------
DROP VIEW IF EXISTS `user_list`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `user_list` AS select i.blid,i.arcimg,a.nicheng,a.user_id from introduce i ,aboutme a where a.user_id = i.user_id ;
